// --- Add useState, useEffect, Sun, and Moon ---
import React, { useState, useEffect, useRef } from 'react';
import { AlertCircle, CheckCircle, Info, Loader, X, Code, Zap, Sun, Moon } from 'lucide-react';
import './App.css';

// --- Import a dark theme for the code editor ---
import AceEditor from "react-ace";
import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/theme-github";
import "ace-builds/src-noconflict/theme-tomorrow_night"; // <-- Add a dark theme
import "ace-builds/src-noconflict/ext-language_tools";

// ... (const API_URL = ...)

const CodeReviewTool = () => {
  // ... (all your existing states: code, issues, summary, etc.)

  // --- ADD NEW STATE FOR DARK MODE ---
  const [isDarkMode, setIsDarkMode] = useState(false);

  // --- ADD NEW EFFECT TO TOGGLE CLASS ---
  useEffect(() => {
    // Get the <html> element
    const root = window.document.documentElement;

    if (isDarkMode) {
      root.classList.add('dark');
    } else {
      root.classList.remove('dark');
    }
  }, [isDarkMode]); // Rerun this effect when isDarkMode changes

  // ... (rest of your functions: analyzeCode, IssueCard, etc.)

  return (
    // --- ADD dark: classes to your main container ---
    <div className="min-h-screen bg-gray-50 p-6 dark:bg-gray-800">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        {/* --- ADD dark: classes --- */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6 dark:bg-gray-900">
          <div className="flex items-center justify-between mb-4 flex-wrap gap-4">
            <div className="flex items-center gap-3">
              <Code className="text-blue-600" size={32} />
              <div>
                {/* --- ADD dark: classes --- */}
                <h1 className="text-2xl font-bold text-gray-900 dark:text-gray-100">
                  Code Review Tool
                </h1>
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  Static analysis + AI-powered insights
                </p>
              </div>
            </div>

            <div className="flex items-center gap-4 flex-wrap">
              {/* --- ADD THE TOGGLE BUTTON HERE --- */}
              <button
                onClick={() => setIsDarkMode(!isDarkMode)}
                className="p-2 rounded-full text-gray-500 hover:bg-gray-100 dark:text-gray-400 dark:hover:bg-gray-700"
              >
                {isDarkMode ? <Sun size={20} /> : <Moon size={20} />}
              </button>

              <label className="flex items-center gap-2 text-sm dark:text-gray-300">
                {/* ... (checkbox) ... */}
                Use AI Analysis
              </label>
              <button
                onClick={analyzeCode}
                // ... (rest of button props) ...
              >
                {/* ... (button content) ... */}
              </button>
            </div>
          </div>

          {/* ... (rest of your Summary and Error sections) ... */}
          {/* You will need to add dark: classes to them too */}
        </div>

        {/* Main Content */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Code Editor */}
          {/* --- ADD dark: classes --- */}
          <div className="bg-white rounded-lg shadow-sm overflow-hidden dark:bg-gray-900">
            {/* --- ADD dark: classes (or it will be invisible) --- */}
            <div className="bg-gray-800 text-white px-4 py-2 text-sm font-medium dark:bg-gray-700">
              Code Editor
            </div>

            <div className="h-[600px] border-t dark:border-gray-700">
              <AceEditor
                // --- DYNAMICALLY CHANGE THEME ---
                theme={isDarkMode ? "tomorrow_night" : "github"}

                // ... (rest of your AceEditor props) ...
                mode="python"
                value={code}
                onChange={setCode}
                name="CODE_EDITOR_UNIQUE_ID"
                editorProps={{ $blockScrolling: true }}
                setOptions={{
                  useWorker: false,
                  showLineNumbers: true,
                  tabSize: 4,
                  enableBasicAutocompletion: true,
                  enableLiveAutocompletion: true,
                }}
                fontSize={14}
                showPrintMargin={false}
                highlightActiveLine={true}
                style={{ width: '100%', height: '100%' }}
              />
            </div>
          </div>

          {/* Issues Panel */}
          {/* --- ADD dark: classes --- */}
          <div className="bg-white rounded-lg shadow-sm overflow-hidden dark:bg-gray-900">
            <div className="bg-gray-800 text-white px-4 py-2 text-sm font-medium flex items-center justify-between dark:bg-gray-700">
              {/* ... (Issues panel content) ... */}
            </div>
            <div className="h-[600px] overflow-auto p-4">
              {/* ... (IssueCard components will need dark: classes too) ... */}
            </div>
          </div>
        </div>

        {/* ... (Your Modal will also need dark: classes) ... */}
      </div>
    </div>
  );
};

export default CodeReviewTool;
