import React, { useState, useRef } from 'react';
import { AlertCircle, CheckCircle, Info, Loader, X, Code, Zap } from 'lucide-react';
import './App.css';

// --- NEW IMPORTS for the code editor ---
import AceEditor from "react-ace";

// Import the mode (language) and theme you want to use
import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/theme-github"; // A clean, light theme
import "ace-builds/src-noconflict/ext-language_tools"; // For basic autocompletion
